    def tulishasilketxt(self, kalimat):
        with open("hasil.txt","ab") as f:
            for char in kalimat:
                f.write(ord(char).to_bytes(1,byteorder="big"))
            f.write(b'\n')

    def readfilebin(self, filename):
        bytes=[]
        with open(filename, "rb") as f:
            while True:
                b = f.read(1)
                if not b:
                    break
                bytes.append(int.from_bytes(b, byteorder="big"))
        text =""
        for i in range(len(bytes)):
            text+=chr(bytes[i])
        return(text)

    def cipherstring(self, key, text):
        cipher = enkripRC4(key,text)
        ctext = ciphertext(cipher)
        return ctext

    def cipherhex(self, key, text):
        cipher = enkripRC4(key,text)
        hex = cipherhex(cipher)
        return hex

    def cipher64(self, key, text):
        cipher = enkripRC4(key,text) 
        hex = cipherhex(cipher)    
        c64 = cipher64(hex)
        return c64   

